import React from 'react';
import {StyleSheet, Text, View, ScrollView} from 'react-native';

export default class WeatherCard extends React.Component {
    render() {
        return(
            <View>
            {this.props.temp&&
                <View style={styles.card}>
                    <ScrollView style={{width:'100%'}}>
                        <Text style={styles.text_main}> {this.props.temp}°</Text>
                        <Text style={styles.text_fell}>Ощущается как {this.props.feels_like}°</Text>
                        <Text style={styles.text_third}>Влажность</Text>
                        <Text style={styles.text_f}>{this.props.humidity}</Text>
                        <Text style={styles.text_third}>Скорость ветра</Text>
                        <Text style={{
                            textAlign: 'left',
                            fontSize: 15,
                            marginBottom: 20,
                            marginLeft: 20,
                            color: '#d6d6d6',}}>{this.props.wind}</Text>
                    </ScrollView>
                </View>
            }
            </View>
        );
    }
}
const styles = StyleSheet.create({
    card: {
        borderBottomLeftRadius: 40,
        borderBottomRightRadius: 40,
        height:400,
        width: '100%',
        backgroundColor: '#3949ab'
    },
    text_main: {
        textAlign: 'center',
        marginTop: 10,
        fontSize: 200,
        color: '#ffff'
    },
    text_second: {
        textAlign: 'center',
        marginTop: 20,
        marginBottom: 5,
        fontSize: 30,
        color: '#ffff'
    },
    text_fell:{
        textAlign: 'center',
        color:'#d6d6d6',
        fontSize:15,
        marginBottom: 20
    },
    text_third: {
        textAlign: 'left',
        fontSize: 20,
        marginLeft: 20,
        color: '#fff',
    },
    text_f: {
        textAlign: 'left',
        fontSize: 15,
        marginLeft: 20,
        color: '#d6d6d6',
        marginBottom: 20
    }
})
