import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet,Button, Text, View, TextInput, Picker} from 'react-native';


export default function App() {
    const API_KEY = "961b7ded10f2be5cb6b598f0160b2c58";
    const [selectedValue, setSelectedValue] = useState("Krasnoyarsk");
    const state = {
        temp: undefined,
        city: undefined,
        country: undefined,
        sunrise: undefined,
        sunset: undefined,
        eror: undefined
    }
    const getWeatherCurrent = async (event) => {
        event.preventDefault();
        const url = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${selectedValue}&appid=${API_KEY}`);
        const data = await url.json();
        console.log(data);
        this.setState({
            temp: data.main.temp


        });
    }
    return (
        <View>
            <StatusBar style="light"/>
            <View style={styles.NavBar}>
                <Text style={styles.text_Nav}>Погода</Text>
            </View>
            <View style={styles.container}>
                <Text style={styles.text}>Выберите город</Text>
                <Picker
                    selectedValue={selectedValue}
                    style={{height: 50, width: '80%', textAlign: 'center'}}
                    onValueChange={(itemValue, itemIndex) =>(setSelectedValue(itemValue),getWeatherCurrent(event))}
                >
                    <Picker.Item label="Москва" value="Moscow"/>
                    <Picker.Item label="Санк-Петербург" value="Saint Petersburg"/>
                    <Picker.Item label="Новосибирск" value="Novosibirsk"/>
                    <Picker.Item label="Красноярск" value="Krasnoyarsk"/>
                </Picker>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
      container: {
          marginTop:10,
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
      },

    btn: {
        marginTop: 40,
        width: '60%',
        backgroundColor: '#00aeef',
        borderColor: 'red',
        borderWidth: 5,
        borderRadius: 15
    },
      text: {
        fontSize: 20,
          marginBottom: 5
      },
      NavBar: {
        height:200,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#3949ab',
        paddingBottom: 10,
        borderBottomLeftRadius: 40,
        borderBottomRightRadius: 40
      },
        input: {
            width: '100%',
            borderStyle: 'solid',
            borderBottomColor: '#2196f3',
            textAlign: 'center',
            marginTop: 40,
            marginBottom: 20
        },
      text_Nav: {
        marginTop: 20,
        color:'white',
        fontSize:40,
      }
    }
);
