import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet,Button, Text, View, TextInput} from 'react-native';

export default function App() {
    const  API_KEY ="2fda6428-b0a8-400f-bf31-26d07bef20fc";
    const [selectedValue, setSelectedValue] = useState("java");

  return (
      <View>
        <StatusBar style="light" />
        <View style = {styles.NavBar}>
          <Text style = {styles.text_Nav}>Погода</Text>
        </View>
          <View style={styles.container}>
              <TextInput style={styles.input} placeholder='Введите название населенного пункта'/>
              <Picker
                  selectedValue={selectedValue}
                  style={{ height: 50, width: 150 }}
                  onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
              >
                  <Picker.Item label="Москва" value="Moscow" />
                  <Picker.Item label="Новосибирск" value="Novosibirsk" />
                  <Picker.Item label="Санк-Петербург    " value="Berezovka" />
                  <Picker.Item label="JavaScript" value="Krasnoyarsk" />
              </Picker>
          </View>
          <Button style={styles.btn} title='Вычислить'/>

      </View>
  );
}

const styles = StyleSheet.create({
      container: {
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
      },

    btn: {
        marginTop: 40,
        width: '60%',
        backgroundColor: '#00aeef',
        borderColor: 'red',
        borderWidth: 5,
        borderRadius: 15
    },
      text: {
        fontSize: 20,
          marginBottom: 0
      },
      NavBar: {
        height:200,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#3949ab',
        paddingBottom: 10,
        borderBottomLeftRadius: 40,
        borderBottomRightRadius: 40
      },
        input: {
            width: '100%',
            borderStyle: 'solid',
            borderBottomColor: '#2196f3',
            textAlign: 'center',
            marginTop: 40,
            marginBottom: 20
        },
      text_Nav: {
        marginTop: 20,
        color:'white',
        fontSize:40,
      }
    }
);
