import React from 'react';
import {StyleSheet, Text, View, ScrollView} from 'react-native';

export default class WeatherCard extends React.Component {
    render() {
        return(
            <View>
            {this.props.temp&&
                <View style={styles.card}>
                    <ScrollView>
                        <Text></Text>
                        <Text style={styles.text_main}> {this.props.temp}°</Text>
                        <Text style={styles.text_second}>Макс.{this.props.temp_max}°, мин.{this.props.temp_min}°</Text>
                        <Text style={styles.text_second}>Влажность</Text>
                        <Text style={styles.text_second}>{this.props.humidity}</Text>
                        <Text style={styles.text_second}>Скорость ветра</Text>
                        <Text style={styles.text_second}>{this.props.wind}</Text>
                    </ScrollView>
                </View>
            }
            </View>
        );
    }
}
const styles = StyleSheet.create({
    card: {
        alignItems: 'center',
        borderRadius: 40,
        height:40,
        width: '100%',
        backgroundColor: '#3949ab',
        flex: 1,
        justifyContent: 'flex-end',
    },
    text_main: {
        textAlign: 'center',
        marginTop: 20,
        fontSize: 200,
        color: '#ffff'
    },
    text_second: {
        textAlign: 'center',
        marginTop: 20,
        fontSize: 30,
        color: '#ffff'
    },
    text_third: {
        textAlign: 'left',
        marginTop: 20,
        fontSize: 35,
        marginLeft: 20,
        color: '#ffff'
    }
})
