import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet,SafeAreaView, Text, View, Picker} from 'react-native';

export default function App() {
  const [selectedValue, setSelectedValue] = useState("krsk");
  return (
      <View>
        <StatusBar style="light" />
        <View style = {styles.navbar}>
          <Text style = {styles.textNav}>Погода</Text>
        </View>
        <View style={styles.container}>
          <Text style={styles.text}>Выберите город:</Text>
          <Picker
              selectedValue={selectedValue}
              style={{ height: 30, width: 180}}
              onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
          >
            <Picker.Item label="Сосновоборск" value="sos" />
            <Picker.Item label="Дивногорск" value="div" />
            <Picker.Item label="Берёзовка" value="ber" />
            <Picker.Item label="Красноярск" value="krsk" />
          </Picker>
        </View>
        <View style={styles.card}>

        </View>
      </View>
  );
}

const styles = StyleSheet.create({
      container: {
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center',

      },
      text: {
        fontSize: 20,
        marginTop: 20,
        marginBottom: 20
      },
      card: {
        height:100,
        alignItems: 'center',
        justifyContent: 'flex-end',
        backgroundColor: '#3949ab',
        paddingBottom: 10,
        borderRadius: 20,
        margin: 30,
        marginTop: 100
      },
      navbar: {
        height:200,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#3949ab',
        paddingBottom: 10,
        borderBottomLeftRadius: 40,
        borderBottomRightRadius: 40
      },
      textNav: {
        marginTop: 20,
        color:'white',
        fontSize:40,
      }
    }
);
