import { StatusBar } from 'expo-status-bar';
import ReactDOM from 'react-dom'
import React, { useState } from 'react';
import { StyleSheet,Text, View, Picker,ScrollView} from 'react-native';
import WeatherCard from './src/WeatherCard';


export default class App extends React.Component {
    state = {
        temp: undefined,
        temp_max: undefined,
        temp_min: undefined,
        feels_like: undefined,
        speed: undefined,
        humidity: undefined
    }

    API_KEY = "961b7ded10f2be5cb6b598f0160b2c58";
    getWeatherCurrent = async ( value='Moscow') => {
        const url = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${value}&appid=${this.API_KEY}`);
        const data = await url.json();
        console.log(data);
        this.setState({
            temp: (data.main.temp-273.15),
            temp_max: (data.main.temp_max-273.15),
            temp_min: (data.main.temp_min-273.15),
            feels_like: (data.main.feels_like-273.15),
            speed: (data.wind.speed +'м/с'),
            humidity: (data.main.humidity +'м/с')
        });
    }
    render() {

        return (
            <View>
                <StatusBar style="light"/>
                <View style={styles.NavBar}>
                    <Text style={styles.text_Nav}>Погода</Text>
                </View>
                <View style={styles.container}>
                    <Text style={styles.text}>Выберите город</Text>
                    <Picker
                        selectedValue={selectedValue}
                        style={{height: 50, width: '80%', textAlign: 'center'}}
                        onValueChange={(itemValue, itemIndex) => {this.getWeatherCurrent(itemValue)}}
                    >
                        <Picker.Item label="Москва" value="Moscow"/>
                        <Picker.Item label="Санк-Петербург" value="Saint Petersburg"/>
                        <Picker.Item label="Новосибирск" value="Novosibirsk"/>
                        <Picker.Item label="Красноярск" value="Krasnoyarsk"/>
                    </Picker>
                </View>
                    <WeatherCard
                        temp = {this.state.temp}
                        temp_max = {this.state.temp_max}
                        temp_min = {this.state.temp_min}
                        feels_like = {this.state.feels_like}
                        temp_min = {this.state.speed}
                        humidity = {this.state.humidity}
                    />

            </View>
        );
    }
}

const styles = StyleSheet.create({
      container: {
          marginTop:10,
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
          marginBottom: 20
      },
      text: {
        fontSize: 20,
          marginBottom: 5
      },
      NavBar: {
        height:200,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#3949ab',
        paddingBottom: 10,
        borderBottomLeftRadius: 40,
        borderBottomRightRadius: 40
      },
        input: {
            width: '100%',
            borderStyle: 'solid',
            borderBottomColor: '#2196f3',
            textAlign: 'center',
            marginTop: 40,
            marginBottom: 20
        },
      text_Nav: {
        marginTop: 20,
        color:'white',
        fontSize:40,
      }
    }
);
